mkPermute :: String -> [CType] -> [CExpr] -> [CExpr] -> CTranslUnit
mkPermute name ty combine index =
  CTranslUnit
    ( mkTupleTypeAsc 2 ty ++
    [ mkIgnore (-1)
    , mkIndexFun index
    , mkApply 2 combine
    , permute name ])
    (mkNodeInfo (initPos "permute.cu") (Name 0))

