mkPermute :: String -> [CTypeSpec] -> CExpr -> CExpr -> CTranslUnit
mkPermute name ty combine index =
  CTranslUnit
    [ mkTypedef "T"     ty
    , mkTypedef "Ix"    [CUnsigType internalNode, CIntType internalNode]
    , mkIgnore (-1)
